import { useCallback, useRef, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const modelState = { Welcome: 0, NewGame: 1, Closed: 2 };
  const [model, setModel] = useState(modelState.Closed);
  const [players, setPlayers] = useState({
    player1: { name: 'Michael', score: 12 },
    player2: { name: 'Michael', score: 12 },
  });
  const hand1 = useRef();
  const hand2 = useRef();
  const onPlayerHand = useCallback((player) => {
    player.current.classList.remove('spin');
    player.current.classList.add('spin');
  });

  return (
    <div className="app">
      {model != modelState.Closed && (
        <div className="model">
          <div className="container">
            <h2>Welcome to Rocky, Paper, and Sissors!</h2>
            {model == modelState.Welcome && (
              <div className="controls">
                <button onClick={() => setModel(modelState.NewGame)}>New Game</button>
                <button>Load Game</button>
              </div>
            )}
            {model == modelState.NewGame && (
              <form onSubmit={() => setModel(modelState.Closed)}>
                <label>Player 1: <input name="player1" type="text" required /></label>
                <label>Player 2: <input name="player2" type="text" required /></label>
                <button>Start Game</button>
              </form>
            )}
          </div>
        </div>
      )}
      <div className="game">
        <div className="header">
          <p><span>Player 1:</span> {players.player1.name} - <span>Score:</span> {players.player1.score}</p>
          <p>vs</p>
          <p><span>Player 2:</span> {players.player2.name} - <span>Score:</span> {players.player2.score}</p>
        </div>
        <div className="container">
          <div class="player">
            <div ref={hand1} className="hand" onClick={() => onPlayerHand(hand1)}></div>
          </div>
          <div class="player">
            <div ref={hand2} className="hand" onClick={() => onPlayerHand(hand2)}></div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
