import { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const modelState = { Welcome: 0, NewGame: 1, Closed: 2 };
  const [model, setModel] = useState(modelState.Closed);

  return (
    <div className="app">
      {model != modelState.Closed && (
        <div className="model">
          <div className="container">
            <h2>Welcome to Rocky, Paper, and Sissors!</h2>
            {model == modelState.Welcome && (
              <div className="controls">
                <button onClick={() => setModel(modelState.NewGame)}>New Game</button>
                <button>Load Game</button>
              </div>
            )}
            {model == modelState.NewGame && (
              <form onSubmit={() => setModel(modelState.Closed)}>
                <label>Player 1: <input name="player1" type="text" required /></label>
                <label>Player 2: <input name="player2" type="text" required /></label>
                <button>Start Game</button>
              </form>
            )}
          </div>
        </div>
      )}
      <div className="game">
        <div className="header">
          <p><span>Player 1:</span> James - <span>Score:</span> 12</p>
          <p>vs</p>
          <p><span>Player 2:</span> Peter - <span>Score:</span> 23</p>
        </div>
        <div className="container">
          <div class="player">
            <div className="hand">Rocky</div>
          </div>
          <div class="player">
            <div className="hand">Rocky</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
