import { useCallback, useRef, useState } from 'react';
import logo from './logo.svg';
import { CONST } from './App.constant';
import './App.css';

function App() {
  const game = localStorage.getItem('rockpapersissors');
  const [hasGameStarted, setGameStarted] = useState(false);
  const [configNewGame, setConfigNewGame] = useState(false);

  const [activePlayer, setActivePlayer] = useState(CONST.player1);
  const [players, updatePlayers] = useState([
    {name:CONST.player1Name,score:CONST.player1Score},
    {name:CONST.player2Name,score:CONST.player2Score},
  ]);

  const isActivePlayer = useCallback((index) => index == activePlayer);
  const getPlayerName = useCallback((index) => players[index].name);
  const getPlayerScore = useCallback((index) => players[index].score);
  const getPlayerHeaderHTML = useCallback((index) =>
    <p className={isActivePlayer(index) && 'active'}><span>{CONST[`player${index+1}Label`]}:</span> {getPlayerName(index)} - <span>Score:</span> {getPlayerScore(index)}</p>);
  const getPlayerNameFieldHTML = useCallback((index) => 
    <label>{CONST.formLabel} {index}: <input name={`player${index}`} type="text" required /></label>);
  const onStartNewGame = (event) => {
    event.preventDefault();
    console.log(event);
    return false;
  };


  // const choices = ['rock', 'paper', 'sissors'];
  // const [model, setModel] = useState('CLOSED');
  // const [players, setPlayers] = useState({
  //   player1: { name: 'Michael', score: 12 },
  //   player2: { name: 'Michael', score: 12 },
  // });
  // const hand1 = useRef();
  // const hand2 = useRef();
  // const onPlayerHand = useCallback((player) => {
  //   const choice = choices[Math.floor(Math.random() * 3)];
  //   player.current.classList.remove('rock', 'paper', 'sissors');
  //   setTimeout(() => player.current.classList.add(choice), 1);
  // });

  return (
    <div className="app">
        {!hasGameStarted && <div className="model">
          <div className="container">
            <h2>{CONST.title}</h2>
              {configNewGame ? <form onSubmit={(event) => onStartNewGame(event)}>
                {[1,2].map((index) => getPlayerNameFieldHTML(index))}
                <button>{CONST.startGameBtn}</button>
              </form> : <div className="controls">
                <button onClick={() => setConfigNewGame(true)}>{CONST.newGameBtn}</button>
                <button disabled="!game">{CONST.loadGameBtn}</button>
              </div>}
          </div>
        </div>}
      {hasGameStarted && <div className="game">
        <div className="header">
          {getPlayerHeaderHTML(CONST.player1)}
          <p>vs</p>
          {getPlayerHeaderHTML(CONST.player2)}
        </div>
        <div className="container">
          <div className="player">
            <div  className="hand"></div>
          </div>
          <div className="player">
            <div  className="hand"></div>
          </div>
        </div>
      </div>}
    </div>
  );
}

export default App;
